mapper.select:
select count(*)
from discussion_agreeinfo
where b_no = #{b_no} and u_email = #{u_email}

mapper.update:
update discussion_agreeinfo
set agreenum = #{agreenum}
where b_no = #{b_no} and u_email = #{u_email}

mapper.select:
select b_serialno, b_no, b_agreementcount, b_oppositioncount
from discussion_board 
where b_no = #{b_no}
