mapper.update:
update user_info set
u_address = #{u_address}, u_detail = #{u_detail}, u_local = #{u_local}
where u_email = #{u_email}

update user_info set
u_pw = #{u_pw}
where u_email = #{u_email}

update user_info set
u_withdrawdate = sysdate
where u_email = #{u_email}

update user_info set
u_withdrawdate = null
where u_email = #{u_email}
