mapper.select:
select * from (select ROWNUM rnum, a.*, (select a_name from area_data where a_no = b_addinfo) a_name, (select d_name from detail_data where a_no = b_addinfo and d_no= b_detailinfo) d_name from (select * from selectdiscussion_board where b_checkeddel = 0 and b_addinfo = #{areaDataVo.a_no}
and b_title like '%' || #{pagingDto.searchKeyword} || '%'
and b_content like '%' || #{pagingDto.searchKeyword} || '%'
and b_writer like '%' || #{pagingDto.searchKeyword} || '%'
order by b_no desc) a) where rnum >= #{pagingDto.startRow} and rnum <=#{pagingDto.endRow}

mapper.update:
update selectdiscussion_board
set b_readcount = b_readcount + 1
where b_no = #{b_no}

update selectdiscussion_board 
set b_title = #{b_title},
	b_content = #{b_content}
where b_no = #{b_no}

update selectdiscussion_board
set b_checkeddel = 1
where b_no = #{b_no}

mapper.select:
select *
from selectdiscussion_board_resinfo
where u_email = #{u_email} and b_no = #{b_no}

select count(*)
from selectdiscussion_board_resinfo
where u_email = #{u_email} and b_no = #{b_no}

select count(*)
from selectdiscussion_board_resinfo
where b_no = #{b_no}
